Here's a script opus created based on the logic in the PR for analyzing duplication based on message_id and request_id concatenation.
#!/usr/bin/env node

import { readdir, readFile } from 'node:fs/promises';
import { join } from 'node:path';
import { homedir } from 'node:os';
import { existsSync } from 'node:fs';

const CLAUDE_PATH = join(homedir(), '.claude', 'projects');

// ANSI color codes
const colors = {
	reset: '\x1b[0m',
	red: '\x1b[31m',
	green: '\x1b[32m',
	yellow: '\x1b[33m',
	blue: '\x1b[34m',
	magenta: '\x1b[35m',
	cyan: '\x1b[36m',
	gray: '\x1b[90m',
	bold: '\x1b[1m',
};

async function getAllJsonlFiles(dir) {
	const files = [];

	async function scanDir(currentDir) {
		try {
			const entries = await readdir(currentDir, { withFileTypes: true });

			for (const entry of entries) {
				const fullPath = join(currentDir, entry.name);

				if (entry.isDirectory()) {
					await scanDir(fullPath);
				} else if (entry.isFile() && entry.name.endsWith('.jsonl')) {
					files.push(fullPath);
				}
			}
		} catch (error) {
			console.error(`Error scanning directory ${currentDir}:`, error.message);
		}
	}

	await scanDir(dir);
	return files;
}

async function parseJsonlFile(filePath) {
	const entries = [];

	try {
		const content = await readFile(filePath, 'utf-8');
		const lines = content.split('\n').filter(line => line.trim());

		for (const line of lines) {
			try {
				const data = JSON.parse(line);
				if (data.message?.id && data.requestId) {
					entries.push({
						messageId: data.message.id,
						requestId: data.requestId,
						timestamp: data.timestamp,
						uuid: data.uuid,
						parentUuid: data.parentUuid,
						inputTokens: data.message.usage?.input_tokens || 0,
						outputTokens: data.message.usage?.output_tokens || 0,
						cacheCreationTokens: data.message.usage?.cache_creation_input_tokens || 0,
						cacheReadTokens: data.message.usage?.cache_read_input_tokens || 0,
						costUSD: data.costUSD,
						model: data.model,
						filePath,
					});
				}
			} catch (e) {
				// Skip invalid JSON lines
			}
		}
	} catch (error) {
		console.error(`Error reading file ${filePath}:`, error.message);
	}

	return entries;
}

function formatBytes(bytes) {
	if (bytes === 0) return '0 B';
	const k = 1024;
	const sizes = ['B', 'KB', 'MB', 'GB'];
	const i = Math.floor(Math.log(bytes) / Math.log(k));
	return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
}

function getProjectFromPath(filePath) {
	const relativePath = filePath.replace(CLAUDE_PATH + '/', '');
	const parts = relativePath.split('/');
	return parts[0] || 'unknown';
}

function getSessionFromPath(filePath) {
	const fileName = filePath.split('/').pop();
	return fileName?.replace('.jsonl', '') || 'unknown';
}

async function analyzeDuplicates() {
	console.log(`${colors.bold}${colors.blue}Claude Code Duplicate Analysis${colors.reset}\n`);
	console.log(`Scanning directory: ${colors.cyan}${CLAUDE_PATH}${colors.reset}\n`);

	if (!existsSync(CLAUDE_PATH)) {
		console.error(`${colors.red}Error: Claude projects directory not found at ${CLAUDE_PATH}${colors.reset}`);
		process.exit(1);
	}

	// Get all JSONL files
	const files = await getAllJsonlFiles(CLAUDE_PATH);
	console.log(`Found ${colors.green}${files.length}${colors.reset} JSONL files\n`);

	// Parse all entries
	const allEntries = [];
	let totalEntries = 0;

	for (const file of files) {
		const entries = await parseJsonlFile(file);
		allEntries.push(...entries);
		totalEntries += entries.length;
	}

	console.log(`Parsed ${colors.green}${totalEntries}${colors.reset} entries with message IDs and request IDs\n`);

	// Group by message ID + request ID
	const duplicateGroups = new Map();

	for (const entry of allEntries) {
		const key = `${entry.messageId}:${entry.requestId}`;
		if (!duplicateGroups.has(key)) {
			duplicateGroups.set(key, []);
		}
		duplicateGroups.get(key).push(entry);
	}

	// Find actual duplicates (more than 1 occurrence)
	const duplicates = Array.from(duplicateGroups.entries())
		.filter(([_, entries]) => entries.length > 1)
		.sort((a, b) => b[1].length - a[1].length); // Sort by number of duplicates

	if (duplicates.length === 0) {
		console.log(`${colors.green}No duplicates found!${colors.reset}`);
		return;
	}

	console.log(`${colors.red}Found ${colors.bold}${duplicates.length}${colors.reset}${colors.red} unique duplicate combinations${colors.reset}\n`);

	// Calculate impact
	let totalDuplicateEntries = 0;
	let totalWastedTokens = {
		input: 0,
		output: 0,
		cacheCreation: 0,
		cacheRead: 0,
	};
	let totalWastedCost = 0;

	// Calculate totals first
	for (const [_, entries] of duplicates) {
		totalDuplicateEntries += entries.length - 1;
		for (let i = 1; i < entries.length; i++) {
			totalWastedTokens.input += entries[i].inputTokens;
			totalWastedTokens.output += entries[i].outputTokens;
			totalWastedTokens.cacheCreation += entries[i].cacheCreationTokens;
			totalWastedTokens.cacheRead += entries[i].cacheReadTokens;
			if (entries[i].costUSD) {
				totalWastedCost += entries[i].costUSD;
			}
		}
	}

	// Calculate additional statistics
	const maxDuplicates = Math.max(...duplicates.map(([_, entries]) => entries.length));
	const avgTimeSpan = [];
	for (const [_, entries] of duplicates) {
		if (entries.length > 1) {
			entries.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
			const firstTime = new Date(entries[0].timestamp);
			const lastTime = new Date(entries[entries.length - 1].timestamp);
			const timeDiff = (lastTime - firstTime) / 1000; // in seconds
			avgTimeSpan.push(timeDiff);
		}
	}
	const avgSpan = avgTimeSpan.length > 0 ? avgTimeSpan.reduce((a, b) => a + b, 0) / avgTimeSpan.length : 0;

	// Show Key Findings
	console.log(`${colors.bold}${colors.blue}=== KEY FINDINGS ===${colors.reset}\n`);

	console.log(`${colors.bold}1. Massive Duplicate Problem:${colors.reset}`);
	console.log(`   ${colors.gray}•${colors.reset} ${colors.red}${duplicates.length.toLocaleString()}${colors.reset} unique duplicate combinations found`);
	console.log(`   ${colors.gray}•${colors.reset} ${colors.red}${totalDuplicateEntries.toLocaleString()}${colors.reset} duplicate entries (${colors.red}${((totalDuplicateEntries / totalEntries) * 100).toFixed(1)}%${colors.reset} of all entries!)`);
	console.log(`   ${colors.gray}•${colors.reset} These duplicates would be removed by the deduplication logic`);

	const totalWasted = totalWastedTokens.input + totalWastedTokens.output + totalWastedTokens.cacheCreation + totalWastedTokens.cacheRead;
	console.log(`\n${colors.bold}2. Token Waste:${colors.reset}`);
	console.log(`   ${colors.gray}•${colors.reset} ${colors.red}${totalWasted.toLocaleString()}${colors.reset} total wasted tokens due to duplicates`);
	console.log(`   ${colors.gray}•${colors.reset} Most waste is in cache read tokens (${colors.yellow}${(totalWastedTokens.cacheRead / 1000000).toFixed(0)}M${colors.reset}) and cache creation tokens (${colors.yellow}${(totalWastedTokens.cacheCreation / 1000000).toFixed(0)}M${colors.reset})`);
	if (totalWastedCost > 0) {
		console.log(`   ${colors.gray}•${colors.reset} Estimated wasted cost: ${colors.red}$${totalWastedCost.toFixed(2)}${colors.reset}`);
	}

	// Project breakdown
	const projectDuplicates = new Map();
	for (const [_, entries] of duplicates) {
		for (const entry of entries) {
			const project = getProjectFromPath(entry.filePath);
			projectDuplicates.set(project, (projectDuplicates.get(project) || 0) + 1);
		}
	}

	const sortedProjects = Array.from(projectDuplicates.entries())
		.sort((a, b) => b[1] - a[1]);

	console.log(`\n${colors.bold}3. Most Affected Projects:${colors.reset}`);
	const topProjects = sortedProjects.slice(0, 4);
	for (const [project, count] of topProjects) {
		const projectName = project.replace(/-Users-ben-Documents-git-(?:ben-vargas-|unsynced-)?/, '');
		const isThisProject = project.includes('ccusage');
		console.log(`   ${colors.gray}•${colors.reset} ${colors.magenta}${projectName}${colors.reset}: ${colors.yellow}${count.toLocaleString()}${colors.reset} duplicate entries${isThisProject ? ' (this project!)' : ''}`);
	}

	console.log(`\n${colors.bold}4. Duplicate Patterns:${colors.reset}`);
	console.log(`   ${colors.gray}•${colors.reset} Duplicates typically occur within seconds (${colors.yellow}${avgSpan.toFixed(0)}-${(avgSpan * 2).toFixed(0)}${colors.reset} second spans)`);
	console.log(`   ${colors.gray}•${colors.reset} Same session files contain multiple duplicates`);
	console.log(`   ${colors.gray}•${colors.reset} Some entries are duplicated up to ${colors.red}${maxDuplicates}${colors.reset} times!`);

	console.log(`\n${colors.gray}${'─'.repeat(60)}${colors.reset}`);

	console.log(`\n${colors.bold}${colors.blue}Detailed Statistics:${colors.reset}`);
	console.log(`  ${colors.gray}Total duplicate groups:${colors.reset} ${duplicates.length}`);
	console.log(`  ${colors.gray}Total duplicate entries:${colors.reset} ${totalDuplicateEntries} (these would be removed)`);
	console.log(`  ${colors.gray}Total entries analyzed:${colors.reset} ${totalEntries}`);
	console.log(`  ${colors.gray}Duplicate rate:${colors.reset} ${((totalDuplicateEntries / totalEntries) * 100).toFixed(2)}%`);

	console.log(`\n${colors.bold}${colors.blue}All Projects with Duplicates:${colors.reset}`);
	const projectsToShow = Math.min(10, sortedProjects.length);
	for (let i = 0; i < projectsToShow; i++) {
		const [project, count] = sortedProjects[i];
		console.log(`  ${colors.magenta}${project}${colors.reset}: ${count} duplicate entries`);
	}
	if (sortedProjects.length > 10) {
		console.log(`  ${colors.gray}... and ${sortedProjects.length - 10} more projects${colors.reset}`);
	}

	// Show a few examples
	console.log(`\n${colors.bold}${colors.yellow}First 10 Duplicate Examples:${colors.reset}\n`);

	// Display first 10 duplicates
	const exampleDuplicates = duplicates.slice(0, 10);
	for (const [key, entries] of exampleDuplicates) {
		const [messageId, requestId] = key.split(':');
		const occurrences = entries.length;

		// Sort entries by timestamp
		entries.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

		console.log(`${colors.bold}${colors.yellow}Duplicate Found:${colors.reset}`);
		console.log(`  ${colors.gray}Message ID:${colors.reset} ${messageId}`);
		console.log(`  ${colors.gray}Request ID:${colors.reset} ${requestId}`);
		console.log(`  ${colors.gray}Occurrences:${colors.reset} ${colors.red}${occurrences}${colors.reset}`);
		console.log(`  ${colors.gray}Model:${colors.reset} ${entries[0].model || 'unknown'}`);
		console.log(`  ${colors.gray}Tokens:${colors.reset} input=${entries[0].inputTokens}, output=${entries[0].outputTokens}, cache_creation=${entries[0].cacheCreationTokens}, cache_read=${entries[0].cacheReadTokens}`);

		console.log(`\n  ${colors.gray}Timestamps and locations:${colors.reset}`);
		for (const entry of entries) {
			const project = getProjectFromPath(entry.filePath);
			const session = getSessionFromPath(entry.filePath);
			const timestamp = new Date(entry.timestamp).toLocaleString();
			console.log(`    ${colors.cyan}${timestamp}${colors.reset} - Project: ${colors.magenta}${project}${colors.reset}, Session: ${colors.blue}${session}${colors.reset}`);
		}

		// Show time span
		if (entries.length > 1) {
			const firstTime = new Date(entries[0].timestamp);
			const lastTime = new Date(entries[entries.length - 1].timestamp);
			const timeDiff = (lastTime - firstTime) / 1000; // in seconds
			console.log(`  ${colors.gray}Time span:${colors.reset} ${timeDiff.toFixed(1)} seconds`);
		}

		console.log('');
	}
}

// Run the analysis
analyzeDuplicates().catch(console.error);
